    function extractArrayLiteral(source, marker) {
        const idx = source.indexOf(marker);
        if (idx === -1) return null;
        const start = source.indexOf('[', idx);
        if (start === -1) return null;
        let depth = 0;
        let inString = false;
        let escape = false;
        let quote = '';
        for (let i = start; i < source.length; i++) {
            const ch = source[i];
            if (inString) {
                if (escape) {
                    escape = false;
                } else if (ch === '\\') {
                    escape = true;
                } else if (ch === quote) {
                    inString = false;
                }
                continue;
            }
            if (ch === '"' || ch === "'") {
                inString = true;
                quote = ch;
                continue;
            }
            if (ch === '[') {
                depth++;
            } else if (ch === ']') {
                depth--;
                if (depth === 0) {
                    return source.slice(start, i + 1);
                }
            }
        }
        return null;
    }

