// ==UserScript==
// @name         小说修改-全图标等高版
// @namespace    http://tampermonkey.net/
// @version      2025-12-29
// @description  转换和下载都使用图标，隐藏前后等高，使用 ┇ 极窄拉手
// @author       Conan
// @match        https://www.cnprose.com/article-detail/*
// @icon         https://www.google.com/s2/favicons?sz=64&domain=cnprose.com
// @grant        none
// @downloadURL https://update.greasyfork.org/scripts/560982/%E5%B0%8F%E8%AF%B4%E4%BF%AE%E6%94%B9-%E5%85%A8%E5%9B%BE%E6%A0%87%E7%AD%89%E9%AB%98%E7%89%88.user.js
// @updateURL https://update.greasyfork.org/scripts/560982/%E5%B0%8F%E8%AF%B4%E4%BF%AE%E6%94%B9-%E5%85%A8%E5%9B%BE%E6%A0%87%E7%AD%89%E9%AB%98%E7%89%88.meta.js
// ==/UserScript==

(function() {
    'use strict';

    // 1. 图标数据 (Base64)
    const iconDownload= ""

    // 你新提供的转换图标 Base64
    const iconRefresh = ""

    // 2. 容器
    const controlPanel = document.createElement('div');
    controlPanel.id = 'my-control-panel';  // ← 关键：添加 id
    controlPanel.style.cssText = `position:fixed; top:20px; left:0; z-index:1000001; display:flex; align-items:stretch; transition:all 0.3s ease; border-radius:0 4px 4px 0; overflow:hidden; height:32px;`;

    const contentArea = document.createElement('div');
    contentArea.style.cssText = `background:#fff; padding:0 8px; border:1px solid #007bff; border-right:none; display:flex; align-items:center; gap:8px; box-shadow:2px 2px 8px rgba(0,0,0,0.1); height:100%; box-sizing:border-box;`;

    // 3. 控件（保持不变）
    const sizeInput = document.createElement('input');
    sizeInput.type = 'number'; sizeInput.value = '15';
    sizeInput.style.cssText = `width:40px; padding:2px; border:1px solid #ddd; text-align:center; border-radius:3px; font-size:13px; height:20px;`;

    const btnReformat = document.createElement('button');
    btnReformat.title = "转换排版";
    btnReformat.style.cssText = `width:24px; height:24px; background:url(data:image/png;base64,${iconRefresh}) no-repeat center; background-size:contain; border:none; cursor:pointer; padding:0;`;

    const btnDownload = document.createElement('button');
    btnDownload.title = "下载TXT";
    btnDownload.style.cssText = `width:24px; height:24px; background:url(data:image/png;base64,${iconDownload}) no-repeat center; background-size:contain; border:none; cursor:pointer; padding:0;`;

    // 4. 拉手 (┇)
    const toggleBtn = document.createElement('div');
    toggleBtn.innerText = '┇';
    toggleBtn.style.cssText = `background:#007bff; color:#fff; width:14px; display:flex; align-items:center; justify-content:center; cursor:pointer; font-size:12px; user-select:none; height:100%; opacity:0.8;`;

    let isExpanded = true;
    function togglePanel() {
        if (isExpanded) {
            contentArea.style.display = 'none';
            toggleBtn.style.opacity = '0.5';
        } else {
            contentArea.style.display = 'flex';
            toggleBtn.style.opacity = '0.8';
        }
        isExpanded = !isExpanded;
    }
    toggleBtn.onclick = togglePanel;

    // 5. 下载 & 转换逻辑
    function downloadTxt() {
        // （保持不变）
        const titleText = document.querySelector('.detail-title')?.innerText.trim() || "文章";
        const wraps = document.querySelectorAll('.free-content-wrap');
        if (wraps.length < 2) return;
        const paragraphs = Array.from(wraps[1].querySelectorAll('p')).map(p => p.innerText.trim()).filter(t => t.length > 0);
        const fullContent = titleText + "\n\n" + paragraphs.map(p => "　　" + p).join("\n\n");
        const blob = new Blob([fullContent.replace("　　微信里点“发现”，扫一下\n\n","").replace("　　二维码便可将本文分享至朋友圈。\n\n","")], { type: 'text/plain;charset=utf-8' });
        const link = document.createElement('a');
        link.href = URL.createObjectURL(blob);
        link.download = `${titleText}.txt`;
        link.click();
    }

    function reformat() {
        const existingView = document.getElementById('my-reader-view');
        if (existingView) existingView.remove();

        const wraps = document.querySelectorAll('.free-content-wrap');
        if (wraps.length < 2) return;

        const fontSize = sizeInput.value + 'px';
        const titleText = document.querySelector('.detail-title')?.innerText || "文章";
        const paragraphs = Array.from(wraps[1].querySelectorAll('p')).map(p => p.innerText.trim()).filter(t => t.length > 0);

        // 修改点1：只隐藏不是控制面板和阅读视图的元素
        Array.from(document.body.children).forEach(child => {
            if (child.id !== 'my-control-panel' && child.id !== 'my-reader-view') {
                child.style.display = 'none';
            }
        });

        const readerView = document.createElement('div');
        readerView.id = 'my-reader-view';
        readerView.style.cssText = `position:absolute; top:0; left:0; width:100%; min-height:100vh; background:#fdfdfd; display:flex; justify-content:center; padding:70px 20px 40px 20px; z-index:999999; font-family:sans-serif;`;
        const contentBox = document.createElement('div');
        contentBox.style.cssText = "width:100%; max-width:850px; line-height:1.8; color:#1a1a1a;";
        const h1 = document.createElement('h1');
        h1.innerText = titleText;
        h1.style.cssText = "text-align:center; font-weight:bold; font-size:28px; margin-bottom:40px; cursor:pointer;";
        h1.onclick = () => location.reload();
        contentBox.appendChild(h1);
        paragraphs.forEach(t => {
            const p = document.createElement('p');
            p.innerText = t.replace("微信里点“发现”，扫一下","").replace("二维码便可将本文分享至朋友圈。","");
            p.style.cssText = `font-size:${fontSize}; text-indent:2em; margin-bottom:12px; text-align:justify;`;
            contentBox.appendChild(p);
        });
        readerView.appendChild(contentBox);
        document.body.appendChild(readerView);

        // 修改点2：确保控制面板在最上层（z-index更高），且保持展开状态
        controlPanel.style.zIndex = '1000002';  // 比阅读视图高
        if (!isExpanded) togglePanel();  // 如果是收起的，展开它
        window.scrollTo(0, 0);
        if (isExpanded) {
            togglePanel();
        }
    }

    // 6. 组装
    btnReformat.onclick = reformat;
    btnDownload.onclick = downloadTxt;
    contentArea.appendChild(sizeInput);
    contentArea.appendChild(btnReformat);
    contentArea.appendChild(btnDownload);
    controlPanel.appendChild(contentArea);
    controlPanel.appendChild(toggleBtn);
    document.body.prepend(controlPanel);  // 加到最前面

})();