    // ==================== DUNGEON REPORT SERVICE ====================
    // Generates dungeon participation reports with violation detection
    // Provides CSV export functionality

    /**
     * Create dungeon report service
     * Generates reports for dungeon participation with violation detection
     * 
     * @param {Object} context - Script context
     * @returns {Object} Dungeon report service
     * 
     * @example
     * const report = await context.dungeonReport.generateReport('instanceId');
     * context.dungeonReport.downloadCSV(report);
     */
    function createDungeonReportService(context) {
        const { logger, dom, storage, numbers, config } = context;
        
        /**
         * Extract leaderboard data from DOM
         * @returns {Array} Array of player entries from leaderboard
         */
        function extractLeaderboard() {
            try {
                // Tentar encontrar tabela de leaderboard na pÃ¡gina
                const table = dom.query('#leaderboard-table, .leaderboard-table, [class*="leaderboard"]');
                if (table) {
                    const rows = dom.queryAll('tr', table);
                    const players = [];
                    
                    rows.forEach((row, index) => {
                        if (index === 0) return; // Skip header
                        
                        const cells = dom.queryAll('td', row);
                        if (cells.length < 3) return;
                        
                        try {
                            const playerName = cells[0]?.textContent?.trim() || 'Unknown';
                            const damageText = cells[1]?.textContent?.trim() || '0';
                            const damage = numbers.parse(damageText);
                            const hitsText = cells[2]?.textContent?.trim() || '0';
                            const hits = numbers.parse(hitsText);
                            const expText = cells[3]?.textContent?.trim() || '0';
                            const exp = numbers.parse(expText);
                            
                            players.push({
                                playerName,
                                totalDamage: damage,
                                monstersHit: hits,
                                totalExp: exp,
                                violations: []
                            });
                        } catch (error) {
                            logger.debug(`[DungeonReport] Error parsing row: ${error.message}`);
                        }
                    });
                    
                    if (players.length > 0) {
                        logger.info(`[DungeonReport] Extracted ${players.length} players from leaderboard`);
                        return players;
                    }
                }
                
                // FALLBACK: Se nÃ£o encontrou tabela HTML, extrair dados do DOM de batalhas atuais
                logger.info('[DungeonReport] Leaderboard table not found, extracting from monster cards...');
                
                // Procurar pela pÃ¡gina de dungeon e extrair dados dos monstros jÃ¡ iniciados
                const monsterCards = dom.queryAll('.monster-card');
                if (monsterCards.length === 0) {
                    logger.warn('[DungeonReport] No monster cards found for extraction');
                    return [];
                }
                
                const playerDamageMap = new Map(); // playerName -> { damage, hits, exp }
                
                monsterCards.forEach(card => {
                    try {
                        // Procurar informaÃ§Ãµes do jogador na card do monstro
                        // Formato: "Damage: X by PlayerName" ou similar
                        const cardText = card.textContent || '';
                        
                        // Procurar padrÃ£o de dano: "X damage by PlayerName"
                        const damageMatch = cardText.match(/(\d{1,3}(?:,\d{3})*(?:\.\d{1,2})?)\s*(?:damage|dmg)\s*(?:by|from)?\s*([A-Za-z0-9_\s]+)/i);
                        if (!damageMatch) return;
                        
                        const damageStr = damageMatch[1];
                        const playerName = (damageMatch[2] || 'Unknown').trim();
                        const damage = numbers.parse(damageStr);
                        
                        if (!playerDamageMap.has(playerName)) {
                            playerDamageMap.set(playerName, {
                                damage: 0,
                                hits: 0,
                                exp: 0
                            });
                        }
                        
                        const playerData = playerDamageMap.get(playerName);
                        playerData.damage += damage;
                        playerData.hits += 1;
                        
                        // Tentar extrair EXP (formato: "+X exp")
                        const expMatch = cardText.match(/\+(\d{1,3}(?:,\d{3})*)\s*exp/i);
                        if (expMatch) {
                            playerData.exp += numbers.parse(expMatch[1]);
                        }
                    } catch (error) {
                        logger.debug(`[DungeonReport] Error parsing card: ${error.message}`);
                    }
                });
                
                // Converter para array de players
                const players = Array.from(playerDamageMap.entries()).map(([playerName, data]) => ({
                    playerName,
                    totalDamage: data.damage,
                    monstersHit: data.hits,
                    totalExp: data.exp,
                    violations: []
                }));
                
                logger.info(`[DungeonReport] Extracted ${players.length} players from monster cards`);
                return players;
            } catch (error) {
                logger.error(`[DungeonReport] Error extracting leaderboard: ${error.message}`);
                return [];
            }
        }
        
        /**
         * Export report to CSV format
         * @param {Object} report - Report object
         * @returns {string} CSV content
         */
        function exportToCSV(report) {
            if (!report) {
                logger.warn('[DungeonReport] Cannot export null report');
                return '';
            }
            
            const headers = ['Player', 'Total Damage', 'Monsters Hit', 'Total EXP', 'Violations'];
            const rows = report.players.map(p => [
                p.playerName,
                numbers.format(p.totalDamage),
                p.monstersHit,
                numbers.format(p.totalExp),
                p.violations.join('; ') || 'None'
            ]);
            
            const csvContent = [
                `Dungeon Report - Instance ${report.instanceId}`,
                `Generated: ${report.timestamp}`,
                `Total Participants: ${report.totalParticipants}`,
                `Total Violations: ${report.totalViolations}`,
                `Total Damage: ${numbers.format(report.aggregates.totalDamage)}`,
                `Total EXP: ${numbers.format(report.aggregates.totalExp)}`,
                `Total Monsters Hit: ${report.aggregates.totalMonstersHit}`,
                '',
                headers.join(','),
                ...rows.map(r => r.map(cell => `"${cell}"`).join(','))
            ].join('\n');
            
            return csvContent;
        }
        
        /**
         * Download report as CSV file
         * @param {Object} report - Report object
         */
        function downloadCSV(report) {
            const csvContent = exportToCSV(report);
            const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
            const link = document.createElement('a');
            const url = URL.createObjectURL(blob);
            
            link.setAttribute('href', url);
            link.setAttribute('download', `dungeon-report-${report.instanceId}-${Date.now()}.csv`);
            link.style.visibility = 'hidden';
            
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            
            logger.info('[DungeonReport] CSV downloaded successfully');
        }
        
        return {
            extractLeaderboard,
            exportToCSV,
            downloadCSV
        };
    }

