                const ratioText = ratioParts.length ? ` (${ratioParts.join(' â€¢ ')})` : '';
                const metrics = parts.length ? parts.join(' â€¢ ') : null;
                entries.push(metrics ? `${name}: ${metrics}${ratioText}` : `${name}${ratioText}`);
