// ==UserScript==
// @name        jd
// @description Compare price for jd.com
// @namespace   https://greasyfork.org/zh-CN/scripts/jd
// @version     0.22
// @include    http://item.jd.com/*
// @require	http://code.jquery.com/jquery-2.1.1.min.js
// @grant          GM_setClipboard
// @grant          GM_openInTab
// @grant          GM_xmlhttpRequest
// @grant          GM_addStyle
// @grant          unsafeWindow
// @downloadURL https://update.greasyfork.org/scripts/6082/jd.user.js
// @updateURL https://update.greasyfork.org/scripts/6082/jd.meta.js
// ==/UserScript==
////http://wiki.greasespot.net/Greasemonkey_Manual:API
String.format = function () {
  if (arguments.length == 0)
  return null;
  var str = arguments[0];
  for (var i = 1; i < arguments.length; i++) {
    var re = new RegExp('\\{' + (i - 1) + '\\}', 'gm');
    str = str.replace(re, arguments[i]);
  }
  return str;
};
var debug = function (msg) {
  if (console.log)
  console.log(msg);
   else
  alert(msg);
};
var GM = {
  d: true,
  get: function (url, _onload) {
    if (this.d)
    debug('get:' + url);
    GM_xmlhttpRequest({
      method: 'GET',
      url: url,
      onload: function (response) {
        _onload(response);
      },
      onerror: function () {
        alert('error');
      }
    });
  },
  getJson: function (url, _onload) {
    if (this.d)
    debug('getJson:' + url);
    GM_xmlhttpRequest({
      method: 'POST',
      url: url,
      onload: function (response) {
        _onload(JSON.parse(response.responseText));
      },
      onerror: function () {
        alert('error');
      }
    });
  },
  post: function (url, _onload) {
    if (this.d)
    debug('post:' + url);
    GM_xmlhttpRequest({
      method: 'POST',
      url: url,
      onload: function (response) {
        if (_onload != null)
        _onload(response.responseText);
      },
      onerror: function () {
        alert('error');
      }
    });
  },
  header: function (url, _onload) {
    GM_xmlhttpRequest({
      url: url,
      method: 'HEAD',
      onload: function (response) {
        if (_onload != null)
        _onload(response.responseHeaders);
      }
    });
  }
};
var GB2312UnicodeConverter = {
    ToUnicode: function (str) {
        return escape(str).toLocaleLowerCase().replace(/%u/gi, '\\u');
    }
    , ToGB2312: function (str) {
        return unescape(str.replace(/\\u/gi, '%u'));
    }
};
//---------------------------------------------------------------------------------------------------------
var Yixun_logo = 'data:image/png;base64,';
var Amazon_logo = 'data:image/png;base64,';
var NewEggs_logo = 'data:image/png;base64,';
var Suning_logo = 'data:image/png;base64,';
var Guomei_logo = 'data:image/png;base64,';
var Yihaodian_logo = 'data:image/png;base64,';
var Dangdang_logo = 'data:image/png;base64,';
var parentNode = $('ul#summary li#summary-market');
var jdname = $('div.breadcrumb a').last().html();

function removeGB(str){
  return str.replace(/[\u4e00-\u9fa5]+/g, ' ');
}


function getMyName(name) {
  $('li.search').remove();
  suning(String.format('http://search.suning.com/{0}/cityId=9264', name));
  yixun(name);
  eggs(escape(name));
  amazon(name);
  guomei(name);
  dangdang(removeGB(name));
  yihaodian(name);
  //debug(GB2312UnicodeConverter.ToGB2312(name));
}
function start() {
  var $name = $('<input style=\'border: 2px solid #E4393C;width:256px;\' value=\'' + jdname + '\' />');
  var $btn = $('<input style=\'background-color: #E64346;color: white;height: 20px;margin-left:5px;border:0px;cursor:pointer;\' type=\'button\' id=\'research\' value=\'重搜\'/>');
    var $btn2 = $('<input style=\'background-color: #E64346;color: white;height: 20px;margin-left:5px;border:0px;cursor:pointer;\' type=\'button\' id=\'research\' value=\'整理\'/>');
  $('li#summary-price div.dd').append($name).append($btn).append($btn2);
  $name.keypress(function (event) {
    if (event.keyCode == 13) {
      $btn.click();
    }
  });
  $btn.click(function () {
    getMyName($name.val());
  });
  $btn2.click(function () {
    $name.focus().val(removeGB($name.val()));
  });
  getMyName(jdname);
}
function addResult(shopname, price, url, pdname, itemImg) {
  shopname = '<img style=\'margin-top: 4px;\' src=\'' + eval(shopname + '_logo') + '\' />';
  parentNode.before(String.format('<li class=\'search\'><a href=\'{0}\' target=\'_blank\'><div class=\'dt\' style=\'line-height: 32px;text-align: center;\'>{1}</div><div class=\'dd\'><strong style=\'color:red\'>{2}</strong>{3}<img src=\'{4}\' height=\'32px\'></img></div></a></li>', url, shopname, price, pdname, itemImg));
}
function suning(u) {
  //alert(u);
  GM.get(u, function (data) {
    if (data.responseText.indexOf('<html>') == 0) {
      // url redirected
      suning(data.responseText.match(/\bhttp:\/\/\b.*\b/));
      return;
    }
    var first = $(data.responseText).find('div#proShow ul.container.clearfix li:first');

    if (first.html() == null) {
      return;
    }

    var priceImg = first.find('p.price img:first').attr('src2');
    var price = '￥<img src=\'' + priceImg + '\'></img>';
    var url = first.find('a.search-bl').attr('href');
    var pdname = first.find('div.inforBg span:first').find('p:first').html();
    var itemImg = first.find('img.err-product').attr('src');

    addResult('Suning', price, url, pdname, itemImg);
  });
}
function yixun(name) {
  GM.get(String.format('http://searchex.yixun.com/html?area=1&key={0}', name), function (data) {
    var first = $(data.responseText).find('div.goods li:first');
    if (first.html() == null) {
      return;
    }
    var price = first.find('span.mod_price').html();
    var url = first.find('p.mod_goods_tit a:first').attr('href');
    var itemImg = first.find('a.link_pic img').attr('init_src');
    var pdname = first.find('a.link_pic img').attr('alt');
    addResult('Yixun', price, url, pdname, itemImg);
  });
}
function eggs(name) {
  GM.get(String.format('http://www.newegg.cn/Search.aspx?keyword={0}', name), function (data) {
    var first = $(data.responseText).find('div.catepro li.cls:first');
    if (first.html() == null) {
      return;
    }
    var price = first.find('span.price').html();
    var url = first.find('div.img a:first').attr('href');
    var itemImg = first.find('div.img img:first').attr('src');
    var pdname = first.find('div.img img:first').attr('title');
    addResult('NewEggs', price, url, pdname, itemImg);
  });
}
function amazon(name) {
  GM.get(String.format('http://www.amazon.cn/s/ref=nb_sb_noss_1?__mk_zh_CN=%E4%BA%9A%E9%A9%AC%E9%80%8A%E7%BD%91%E7%AB%99&url=search-alias%3Daps&field-keywords={0}', name), function (data) {
    var first = $(data.responseText).find('div#result_0');
    if (first.html() == null) {
      return;
    }
    var price = first.find('span.bld.lrg.red').html();
    if(price==null){
      price = first.find('span.price.bld').html();
    }
    var url = first.find('div.image.imageContainer a:first').attr('href');
    var itemImg = first.find('div.imageBox img:first').attr('src');
    var pdname = first.find('span.lrg').html();
    addResult('Amazon', price, url, pdname, itemImg);
  });
}
function guomei(name) {
  GM.get(String.format('http://www.gome.com.cn/search?question={0}', name), function (data) {
    var first = $(data.responseText).find('div#prodByAjax li:first');
    if (first.html() == null) {
      return;
    }
    var price = first.find('span.price').html();
    var url = first.find('p.pic-wrap a:first').attr('href');
    var itemImg = first.find('p.pic-wrap img:first').attr('gome-src');
    var pdname = first.find('p.pic-wrap img:first').attr('alt');
    addResult('Guomei', price, url, pdname, itemImg);
  });
}
function dangdang(name) {

	GM.get(String.format("http://search.dangdang.com/?key={0}",name),function(data){

		var first = $(data.responseText).find("li.line1");

    if(first.html()==null){
      return;
    }
 
    var price = first.find("p.price span.price_n").html();
    if(price==null)
      price = first.find("p.price span.search_now_price").html();
      
		var url = first.find("a.pic:first").attr("href");
		var itemImg = first.find("a.pic img:first").attr("data-original");
		var pdname =  first.find("a.pic:first").attr("title");

    addResult('Dangdang',price,url,pdname,itemImg);
  });

}
function yihaodian(name) {
  GM.get(String.format('http://search.yhd.com/s2/c0-0/k{0}/1/?tp=', name), function (data) {
    var first = $(data.responseText).find('div.search_table li.search_item:first');
    if (first.html() == null) {
      return;
    }
    var price = first.find('span.price:first').html();
    var url = first.find('a.search_prod_img').attr('href');
    var itemImg = first.find('a.search_prod_img img:first').attr('src');
    var pdname = first.find('p.title a:first').attr('title');
    addResult('Yihaodian', price, url, pdname, itemImg);
  });
}
start();